// Get currency by code
export const getCurrencyByCode = (code) => {
  return currencies.find((currency) => currency.code === code) || currencies[0];
};

// Format currency amount
export const formatCurrency = (amount, currencyCode = "USD") => {
  const currency = getCurrencyByCode(currencyCode);

  // Special formatting for certain currencies
  const formatOptions = {
    style: "currency",
    currency: currencyCode,
    minimumFractionDigits: 0,
    maximumFractionDigits: 2,
  };

  // Some currencies don't need decimal places
  if (["JPY", "KRW", "VND", "IDR", "CLP", "PYG"].includes(currencyCode)) {
    formatOptions.minimumFractionDigits = 0;
    formatOptions.maximumFractionDigits = 0;
  }

  try {
    return new Intl.NumberFormat("en-US", formatOptions).format(amount);
  } catch {
    // Fallback formatting if currency is not supported by Intl
    return `${currency.symbol}${amount.toLocaleString()}`;
  }
};

